<template>
	<v-container>

		<div class="text-center">
			<v-row>
				<v-col>
					<v-btn icon color="green" @click="refreshLibrary">
						<v-icon>mdi-cached</v-icon>
					</v-btn>
					<v-btn icon color="yellow darken-4" @click="openFolder">
						<v-icon>mdi-folder-open-outline</v-icon>
					</v-btn>
				</v-col>
			</v-row>
		</div>

		<v-item-group>
			<v-row>
				<v-col
					v-for="(item, i) in videos"
					:key="i"
					cols="6"
					md="3"
				>
					<v-item>
						<v-img
							src="data:image/png;base64,"
							height="150"
							class="white--text text-right pa-2"
						>
							<v-btn icon dark>
								<v-icon>
									{{ 'mdi-play' }}
								</v-icon>
							</v-btn>

							<v-card-title>{{ item }}</v-card-title>
						</v-img>
					</v-item>
				</v-col>
			</v-row>
		</v-item-group>
	</v-container>
</template>

<script>
import { shell } from 'electron';
import { readdirSync } from 'fs';

export default {
	name: 'Library',

	data() {
		return {
			appPath: (require('os').homedir() + "\\ScreenRecorder"),
			libPath: "\\Records\\",
			videos: []
		}
	},

	methods: {
		async refreshLibrary() {
			this.videos = [];
			this.videos = await readdirSync(this.appPath + this.libPath);
		},
		openFolder() {
			shell.openItem(this.appPath + this.libPath);
		}
	},
	
	mounted() {
		this.refreshLibrary();
	}
}
</script>
